import {
    type SmartAccountClient,
    createSmartAccountClient
} from "permissionless"
import { toSimpleSmartAccount } from "permissionless/accounts"
import { createPimlicoClient } from "permissionless/clients/pimlico"
import {
    http,
    type Chain,
    type Hex,
    type Transport,
    createPublicClient,
    createTestClient,
    createWalletClient,
    parseEther
} from "viem"
import {
    type EntryPointVersion,
    type SmartAccount,
    entryPoint06Address,
    entryPoint07Address
} from "viem/account-abstraction"
import {
    generatePrivateKey,
    mnemonicToAccount,
    privateKeyToAccount
} from "viem/accounts"
import { foundry } from "viem/chains"
import { ALTO_RPC, ANVIL_RPC } from "./constants"

const anvilClient = createTestClient({
    transport: http(ANVIL_RPC),
    chain: foundry,
    mode: "anvil"
})

export type AAParamType = {
    entryPointVersion: EntryPointVersion
    privateKey?: Hex
}

export const getAnvilWalletClient = (addressIndex: number) => {
    return createWalletClient({
        account: mnemonicToAccount(
            "test test test test test test test test test test test junk",
            {
                addressIndex
            }
        ),
        chain: foundry,
        transport: http(ANVIL_RPC)
    })
}

export const getPimlicoClient = <EntryPointVersion extends "0.6" | "0.7">({
    entryPointVersion
}: {
    entryPointVersion: EntryPointVersion
}) =>
    createPimlicoClient({
        chain: foundry,
        entryPoint: {
            address: (entryPointVersion === "0.6"
                ? entryPoint06Address
                : entryPoint07Address) as EntryPointVersion extends "0.6"
                ? typeof entryPoint06Address
                : typeof entryPoint07Address,
            version: entryPointVersion
        },
        transport: http(ALTO_RPC)
    })

export const getPublicClient = (anvilRpc: string) => {
    const transport = http(anvilRpc, {
        //onFetchRequest: async (request) => {
        //    console.log("fetching", await request.json())
        //}
    })

    return createPublicClient({
        chain: foundry,
        transport: transport,
        pollingInterval: 100
    })
}

export const getSmartAccountClient = async <
    EntryPointVersion extends "0.6" | "0.7"
>({
    entryPointVersion,
    privateKey = generatePrivateKey()
}: AAParamType): Promise<
    SmartAccountClient<Transport, Chain, SmartAccount>
> => {
    const account = await toSimpleSmartAccount<EntryPointVersion>({
        client: getPublicClient(ANVIL_RPC),
        entryPoint: {
            address:
                entryPointVersion === "0.6"
                    ? entryPoint06Address
                    : entryPoint07Address,
            version: (entryPointVersion === "0.6"
                ? "0.6"
                : "0.7") as EntryPointVersion
        },
        owner: privateKeyToAccount(privateKey)
    })

    await anvilClient.setBalance({
        address: account.address,
        value: parseEther("100")
    })

    return createSmartAccountClient({
        account,
        chain: foundry,
        bundlerTransport: http(ALTO_RPC),
        userOperation: {
            estimateFeesPerGas: async () =>
                (
                    await getPimlicoClient({
                        entryPointVersion
                    }).getUserOperationGasPrice()
                ).fast
        }
    })
}

export const setBundlingMode = async (mode: "auto" | "manual") => {
    await fetch(ALTO_RPC, {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            jsonrpc: "2.0",
            method: "debug_bundler_setBundlingMode",
            params: [mode],
            id: 4337
        })
    })
}

export const sendBundleNow = async () => {
    await fetch(ALTO_RPC, {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            jsonrpc: "2.0",
            method: "debug_bundler_sendBundleNow",
            params: [],
            id: 4337
        })
    })
}

export const clearBundlerState = async () => {
    await fetch(ALTO_RPC, {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            jsonrpc: "2.0",
            method: "debug_bundler_clearState",
            params: [],
            id: 4337
        })
    })
}

export const beforeEachCleanUp = async () => {
    await clearBundlerState()
    await setBundlingMode("auto")

    await anvilClient.setAutomine(true)
    await anvilClient.mine({ blocks: 1 })
}

